document.addEventListener("DOMContentLoaded", () => {
    const toggleCheckbox = document.querySelector(".icon-toggle input.checkbox");

    if (!toggleCheckbox) {
        console.error("Checkbox not found! Verify your class name.");
        return;
    }

    // Alternar la creación y destrucción del canvas
    toggleCheckbox.addEventListener("change", (event) => {
        if (event.target.checked) {
            createCanvasInstance();
        } else {
            destroyCanvasInstance();
        }
    });
});

function createCanvasInstance() {
    if (canvasActive) return;

    canvas = createCanvas(window.innerWidth, window.innerHeight);
    canvas.style("position", "absolute");
    canvas.style("z-index", "1000000");
    canvas.style("pointer-events", "none");

    gravity = createVector(0, 5);

    circles = [];
    for (let i = 0; i < numCircles; i++) {
        const radius = calculateRadius();
        const x = random(radius, window.innerWidth - radius);
        const y = random(radius, window.innerHeight / 2);
        const svg = random(svgShapes);

        let newCircle = new Circle(x, y, radius, svg);
        circles.push(newCircle);
    }

    canvasActive = true;
}

function destroyCanvasInstance() {
    if (!canvasActive) return;

    circles.forEach((circle) => {
        circle.svgElement.remove();
    });

    canvas.remove();
    canvas = null;
    circles = [];
    canvasActive = false;
}

function setup() {
    noCanvas();
}

function draw() {
    if (!canvasActive) return;

    clear();

    for (let circle of circles) {
        if (circle !== draggingCircle) {
            circle.applyForce(gravity);
        }
        circle.update();
        circle.edges();
        circle.display();

        for (let other of circles) {
            if (circle !== other) {
                circle.checkCollision(other);
            }
        }
    }
}

function windowResized() {
    if (canvasActive) {
        resizeCanvas(window.innerWidth, window.innerHeight);
        const newRadius = calculateRadius();
        circles.forEach((circle) => circle.updateRadius(newRadius));
    }
}

function mousePressed() {
    if (!canvasActive) return;

    for (let circle of circles) {
        if (circle.contains(mouseX, mouseY)) {
            draggingCircle = circle;
            draggingCircle.vel.set(0);
            break;
        }
    }
}

function mouseDragged() {
    if (!canvasActive || !draggingCircle) return;

    let mouseVel = createVector(mouseX, mouseY).sub(draggingCircle.pos);
    draggingCircle.vel.set(mouseVel.x / 4, mouseVel.y / 5);
    draggingCircle.pos.set(mouseX, mouseY);
}

function mouseReleased() {
    if (!canvasActive) return;
    draggingCircle = null;
}

class Circle {
    constructor(x, y, r, svg) {
        this.pos = createVector(x, y);
        this.vel = createVector(0, 0);
        this.acc = createVector(0, 0);
        this.r = r;

        this.svgElement = document.createElement("div");
        this.svgElement.innerHTML = svg;
        this.svgElement.style.position = "absolute";
        this.svgElement.style.width = `${r * 2}px`;
        this.svgElement.style.height = `${r * 2}px`;
        this.svgElement.style.pointerEvents = "none";
        this.svgElement.style.zIndex = "10000001";

        document.body.appendChild(this.svgElement);
        this.updatePosition();
    }

    applyForce(force) {
        let f = p5.Vector.div(force, this.r);
        this.acc.add(f);
    }

    update() {
        this.vel.add(this.acc);
        this.pos.add(this.vel);
        this.acc.mult(0);
    }

    edges() {
        if (this.pos.y + this.r >= window.innerHeight) {
            this.pos.y = window.innerHeight - this.r;
            this.vel.y *= -0.8;
            this.vel.x *= 0.8;
        } else if (this.pos.y - this.r < 0) {
            this.pos.y = this.r;
            this.vel.y *= -0.8;
        }

        if (this.pos.x + this.r > window.innerWidth) {
            this.pos.x = window.innerWidth - this.r;
            this.vel.x *= -0.8;
        } else if (this.pos.x - this.r < 0) {
            this.pos.x = this.r;
            this.vel.x *= -0.8;
        }
    }

    display() {
        this.updatePosition();
    }

    updatePosition() {
    this.svgElement.style.left = `${this.pos.x - this.r}px`;
    this.svgElement.style.top = `${this.pos.y - this.r + window.scrollY}px`; // Ajusta la posición relativa al scroll
}

    contains(x, y) {
        let d = dist(x, y, this.pos.x, this.pos.y);
        return d < this.r;
    }

    checkCollision(other) {
        let distance = dist(this.pos.x, this.pos.y, other.pos.x, other.pos.y);
        let minDist = this.r + other.r;

        if (distance < minDist) {
            let forceDir = p5.Vector.sub(this.pos, other.pos).normalize();
            let overlap = minDist - distance;

            this.pos.add(forceDir.copy().mult(overlap * 0.5));
            other.pos.sub(forceDir.copy().mult(overlap * 0.5));

            let relativeVelocity = p5.Vector.sub(this.vel, other.vel);
            let speed = relativeVelocity.dot(forceDir);

            if (speed < 0) {
                let impulse = forceDir.mult(speed * -0.5);
                this.vel.add(impulse);
                other.vel.sub(impulse);

                this.vel.mult(0.98);
                other.vel.mult(0.98);
            }
        }
    }

    updateRadius(newRadius) {
        this.r = newRadius;
        this.svgElement.style.width = `${newRadius * 2}px`;
        this.svgElement.style.height = `${newRadius * 2}px`;
        this.updatePosition();
    }
}